mapper.update:
UPDATE notice
SET title = #{title}, content = #{content}, chdate = #{chdate}, view_count = #{viewCount}
WHERE notice_num = #{noticeNum}

mapper.select:
SELECT notice_num, title, content, regdate, chdate, view_count
FROM notice
WHERE notice_num = #{noticeNum}

mapper.update:
UPDATE notice
SET view_count = view_count + 1
WHERE notice_num = #{noticeNum}
