mapper.select:
SELECT movie_id, movie_title, release_date, description, poster_url, trailer_url, total_audience, age_rating, genre
FROM movie
WHERE movie_id = #{movie_id}

mapper.update:
UPDATE movie
SET movie_title = #{movie_title}, release_date = #{release_date}, 
    description = #{description}, poster_url = #{poster_url}, trailer_url = #{trailer_url}, total_audience = #{total_audience},
    age_rating = #{age_rating}, genre = #{genre}
WHERE movie_id = #{movie_id}
