mapper.select:
SELECT notice_num AS noticeNum, title, content, regdate, chdate, view_count AS viewCount
FROM notice
WHERE notice_num = #{noticeNum}

mapper.update:
UPDATE notice
SET view_count = view_count + 1
WHERE notice_num = #{noticeNum}

UPDATE notice
SET title = #{title}, content = #{content}, chdate = #{chdate}
WHERE notice_num = #{noticeNum}
